const base = 'https://js.kristjankivikangur21.thkit.ee/';

// A function to handle sending HTTP requests using fetch
function send({ method, path, data, token }) {
    // Use window.fetch for browser or node-fetch for server
    const fetch = process.browser ? window.fetch : require('node-fetch').default;

    const opts = { method, headers: {} };

    // If there's data, set the Content-Type and include the body
    if (data) {
        opts.headers['Content-Type'] = 'application/json';
        opts.body = JSON.stringify(data);
    }

    // Include the Authorization header if there's a token
    if (token) {
        opts.headers['Authorization'] = `Bearer ${token}`;
    }

    // Send the request to the base URL + path
    return fetch(`${base}/${path}`, opts)
        .then(r => r.text())
        .then(json => {
            try {
                return JSON.parse(json); // Parse the JSON response
            } catch (err) {
                return json; // Return as is if parsing fails
            }
        });
}

// Export helper functions for each HTTP method
export function get(path, token) {
    return send({ method: 'GET', path, token });
}

export function del(path, token) {
    return send({ method: 'DELETE', path, token });
}

export function post(path, data, token) {
    return send({ method: 'POST', path, data, token });
}

export function put(path, data, token) {
    return send({ method: 'PUT', path, data, token });
}
